#!/usr/bin/env python3

import random

enc = []
dec = []
for i in [32, 256, 2048, 16384]:
    enc_data = bytearray([random.randint(0, 255) for _ in range(i)]).hex()
    enc.append(f'pub const ENC_{i}: &[u8; {i}] = &hex!("{enc_data}");')

    dec_data = bytearray([random.randint(0, 255) for _ in range(i // 2)]).hex()
    dec.append(f'pub const DEC_{i}: &str = "{dec_data}";')

enc = f"\n".join(enc)
dec = f"\n".join(dec)
print(
    f"""\
// @generated by gen_data.py, do not modify manually
use hex_literal::hex;

{enc}

{dec}\
"""
)
